import {useState} from "react";

function Square({color, onSquareClick }){
    return (
        <button
        className={`square ${color}`}
        onClick={onSquareClick}
        >
        </button>
    )
}


export default function Board(){
    const [square, setSquare] = useState(Array(7).fill(null))
    const [redIsNext, setRedIsNext] = useState(true);


    function handleClick(i){
        const nextSquares = square.slice();
        
        let colNumber = i % 7;
        console.log(colNumber)
        if(redIsNext){
            nextSquares[i] = "red"
        } else{
            nextSquares[i] = "blue"
        }
    
    setSquare(nextSquares)
    setRedIsNext(!redIsNext)
    }

    return (
    <>
        <div className="board-row">
            <Square color={square[0]} onSquareClick={()=> handleClick(0)}/>
            <Square color={square[1]} onSquareClick={()=> handleClick(1)}/>
            <Square color={square[2]} onSquareClick={()=> handleClick(2)}/>
            <Square color={square[3]} onSquareClick={()=> handleClick(3)}/>            
            <Square color={square[4]} onSquareClick={()=> handleClick(4)}/>
            <Square color={square[5]} onSquareClick={()=> handleClick(5)}/>
            <Square color={square[6]} onSquareClick={()=> handleClick(6)}/>
        </div>
        <div className="board-row">
            <Square color={square[7]} onSquareClick={()=> handleClick(0)}/>
            <Square color={square[8]} onSquareClick={()=> handleClick(1)}/>
            <Square color={square[9]} onSquareClick={()=> handleClick(2)}/>
            <Square color={square[10]} onSquareClick={()=> handleClick(3)}/>            
            <Square color={square[11]} onSquareClick={()=> handleClick(4)}/>
            <Square color={square[12]} onSquareClick={()=> handleClick(5)}/>
            <Square color={square[13]} onSquareClick={()=> handleClick(6)}/>
        </div>
    
    
    </>);
}
